const fs = require\('fs'\);
const html = fs.readFileSync\('C:/Users/raviv/Downloads/2026 2_54 AM EST \(1\).txt', 'utf-8'\);

// Find all 7fee58_ references \(audio file IDs\)
const fileRefs = [...html.matchAll\(/7fee58_[a-f0-9]+/g\)].map\(m => m[0]\);
const uniqueRefs = [...new Set\(fileRefs\)];
console.log\('Unique file refs:', uniqueRefs.length\);
uniqueRefs.forEach\(r => console.log\('  ', r\)\);

